struct ContentView: View {
    var body: some View {
        Text("Hello, World!")
            .font(.title)
            .foregroundStyle(.primary)
    }
}

struct CounterView: View {
    @State private var count = 0

    var body: some View {
        VStack {
            Text("Count: \(count)")
            Button("Increment") {
                count += 1
            }
        }
    }
}

struct ItemListView: View {
    let items: [String]

    var body: some View {
        NavigationStack {
            List(items, id: \.self) { item in
                NavigationLink(item) {
                    Text(item)
                }
            }
            .navigationTitle("Items")
        }
    }
}

